<script>
export default {
  props: {
    logoColor: {
      type: String,
      default: '#000000'
    },

    logoWidth: {
      type: String,
      default: '15rem'
    },

    //  width / height
    logoRate: {
      type: Number,
      default: 0.2777
    }
  },

  computed: {
    logoPaddingTop () {
      let logoWidth = parseInt(this.logoWidth)
      // if this.logoWidth is '', set default value
      if (isNaN(logoWidth)) logoWidth = 15

      return this.logoRate * logoWidth
    }
  }
}
